  var cadenaEntrada = req.body.entrada;
  var array = cadenaEntrada.split(',');
  var array_invalidos = new Array();
  var array_validos = new Array();
  console.log("El arreglo es: ".concat(array));
  array.forEach(function (element) {
    var fecha = element.trim();
    //La validacion es solo para los dias y meses.
    if (fecha.length == 10) {
      var array_fecha = fecha.split('/');
      if (array_fecha.length == 3) {
        var es_valido = parseInt(array_fecha[0]) <= 30 && array_fecha[1] <= 12 ? true : false;
        if (es_valido) array_validos.push(fecha);else array_invalidos.push(fecha);
      } else array_invalidos.push(fecha);
    } else array_invalidos.push(fecha);
  });
  //Se le envia al front un array con las repuestas
  var result = {
    entrda: array,
    valoresInvalidor: array_invalidos,
    valoresValidos: array_validos
  };
  res.send(JSON.stringify(result));
}
function validarEjercicio_2(req, res) {
  var cadena_entrada = req.body.entrada.trim();
  var array_numero = new Array();
  var total = 0;
  for (var index in cadena_entrada) {
    var caracter = cadena_entrada.charAt(index);
    if (!isNaN(caracter)) {
      array_numero.push(caracter);
      total++;
    }
  }
  //Se le envia al front un array con las repuestas
  var result = {
    entrada: cadena_entrada,
    numeros: array_numero,
    total: total
  };
  res.send(JSON.stringify(result));
}

