    #id;
    #nombre;
    #apellidoPaterno;
    #apellidoMaterno;
    #fechaNacimiento;
    constructor(_id,_nombre,_apellidoPaterno,_apellidoMaterno,_fechaNacimiento){
        this.#id = _id;
        this.#nombre = _nombre;
