export class Persona {
    #id;
    #nombre;
    #apellidoPaterno;
    #apellidoMaterno;
    #fechaNacimiento;

    constructor (_id,_nombre,_apellidoPaterno,_apellidoMaterno,_fechaNacimiento){
        this.#id = _id;
        this.#nombre = _nombre;
        this.#apellidoPaterno = _apellidoPaterno;
        this.#apellidoMaterno = _apellidoMaterno;
        this.#fechaNacimiento = _fechaNacimiento;
    }

    getId(){
        this.#id;
    }

    getNombre(){
        return this.#nombre;
    }
}
